// Merge original lead data with Apify enrichment results
const originalLead = $input.first().json;
const enrichmentData = $input.last().json;

return {
  ...originalLead,
  enriched_email: enrichmentData.email_verified || originalLead.email,
  company_size: enrichmentData.company_size,
  company_industry: enrichmentData.industry,
  company_revenue: enrichmentData.revenue_range,
  linkedin_url: enrichmentData.linkedin_url,
  phone: enrichmentData.phone || originalLead.phone,
  job_level: enrichmentData.job_level,
  technologies: enrichmentData.tech_stack,
  funding_stage: enrichmentData.funding,
  enrichment_timestamp: new Date().toISOString()
};

// Lead Scoring Engine - Scores based on ICP fit + engagement
const lead = $input.first().json;

let score = 0;

// Company size scoring (0-25 points)
const sizeMap = {
  '1-10': 5,
  '11-50': 10,
  '51-200': 20,
  '201-1000': 25,
  '1001+': 20
};
score += sizeMap[lead.company_size] || 0;

// Industry fit (0-20 points)
const targetIndustries = ['Software', 'Technology', 'SaaS', 'Finance', 'Healthcare'];
if (targetIndustries.includes(lead.company_industry)) score += 20;

// Job level scoring (0-25 points)
const levelMap = {
  'C-Level': 25,
  'VP': 20,
  'Director': 15,
  'Manager': 10,
  'Individual Contributor': 5
};
score += levelMap[lead.job_level] || 0;

// Data quality (0-15 points)
if (lead.email_valid) score += 5;
if (lead.linkedin_url && lead.linkedin_valid) score += 5;
if (lead.phone) score += 5;

// Intent signals (0-15 points)
const highIntentSources = ['demo_request', 'pricing_page', 'contact_sales'];
if (highIntentSources.includes(lead.source)) score += 15;
else if (lead.source === 'webinar') score += 10;
else score += 5;

// Assign tier
let tier = 'Low';
if (score >= 70) tier = 'Hot';
else if (score >= 50) tier = 'MQL';
else if (score >= 30) tier = 'Nurture';

return {
  ...lead,
  lead_score: score,
  lead_tier: tier,
  is_mql: score >= 50,
  scored_at: new Date().toISOString()
};

