// Intent detection - analyze reply for buying signals
const reply = $input.first().json.reply_text.toLowerCase();

const intentKeywords = {
  high: ['demo', 'pricing', 'price', 'cost', 'budget', 'buy', 'purchase', 'interested', 'calendar', 'meeting', 'call', 'when can', 'next steps'],
  medium: ['tell me more', 'learn more', 'information', 'how does', 'can you', 'would like to know'],
  low: ['thanks', 'thank you', 'interesting', 'maybe', 'later', 'not right now']
};

let intentLevel = 'low';
let intentScore = 0;

for (const keyword of intentKeywords.high) {
  if (reply.includes(keyword)) {
    intentScore += 10;
    intentLevel = 'high';
  }
}

for (const keyword of intentKeywords.medium) {
  if (reply.includes(keyword)) {
    intentScore += 5;
    if (intentLevel === 'low') intentLevel = 'medium';
  }
}

return {
  ...$input.first().json,
  intent_level: intentLevel,
  intent_score: intentScore,
  should_book_meeting: intentScore >= 20
};

// Lead Type Detection - Determine if inbound or outbound
const lead = $input.first().json;

let leadType = 'unknown';
let source = lead.source || '';

// Detection logic
if (lead.list_id || lead.campaign_id || source === 'outbound') {
  leadType = 'outbound';
} else if (lead.form_id || lead.page_url || source === 'inbound' || source.includes('website') || source.includes('landing')) {
  leadType = 'inbound';
} else if (lead.origin && lead.origin.includes('outbound')) {
  leadType = 'outbound';
} else if (lead.origin && lead.origin.includes('inbound')) {
  leadType = 'inbound';
} else {
  // Default: check if enriched contact data present (outbound) or minimal form data (inbound)
  leadType = lead.company_domain ? 'outbound' : 'inbound';
}

return {
  ...lead,
  detected_type: leadType,
  detection_timestamp: new Date().toISOString(),
  // Normalize field names
  email: lead.email || lead.contact_email,
  name: lead.name || lead.contact_name,
  company: lead.company || lead.company_name,
  title: lead.job_title || lead.contact_title || lead.title
};

// Centralized Lead Scoring Engine
// Used by both inbound and outbound flows
const lead = $input.first().json;

let score = 0;
const scoreBreakdown = {};

// 1. COMPANY FIT (0-30 points)
const sizeScores = {'1-10': 5, '11-50': 10, '51-200': 20, '201-1000': 25, '1001+': 20};
const companyScore = sizeScores[lead.company_size] || 0;
score += companyScore;
scoreBreakdown.company_size = companyScore;

const targetIndustries = ['Software', 'Technology', 'SaaS', 'Finance', 'Healthcare', 'E-commerce'];
const industryScore = targetIndustries.includes(lead.company_industry) ? 15 : 0;
score += industryScore;
scoreBreakdown.industry = industryScore;

// 2. CONTACT QUALITY (0-30 points)
const levelScores = {'C-Level': 25, 'VP': 20, 'Director': 15, 'Manager': 10, 'IC': 5};
const levelScore = levelScores[lead.job_level] || 0;
score += levelScore;
scoreBreakdown.job_level = levelScore;

let dataScore = 0;
if (lead.email_valid) dataScore += 5;
if (lead.linkedin_url && lead.linkedin_valid) dataScore += 5;
if (lead.phone && lead.phone_valid) dataScore += 5;
score += dataScore;
scoreBreakdown.data_quality = dataScore;

// 3. INTENT & ENGAGEMENT (0-25 points)
let intentScore = 0;
if (lead.detected_type === 'inbound') {
  const highIntentSources = ['demo_request', 'pricing_page', 'contact_sales', 'free_trial'];
  const mediumIntentSources = ['webinar', 'whitepaper', 'case_study'];
  if (highIntentSources.includes(lead.source)) intentScore = 20;
  else if (mediumIntentSources.includes(lead.source)) intentScore = 10;
  else intentScore = 5;
} else {
  // Outbound: score based on research quality
  if (lead.personalization_score > 60) intentScore = 15;
  else if (lead.personalization_score > 30) intentScore = 10;
  else intentScore = 5;
}
score += intentScore;
scoreBreakdown.intent = intentScore;

// 4. TIMING & SIGNALS (0-15 points)
let timingScore = 0;
if (lead.recent_news && lead.recent_news.includes('funding')) timingScore += 10;
if (lead.tech_stack && lead.tech_stack.includes('competitor')) timingScore += 5;
if (lead.hiring_growth === 'high') timingScore += 5;
score += Math.min(timingScore, 15);
scoreBreakdown.timing = timingScore;

// Determine tier
let tier = 'Low';
let is_mql = false;
if (score >= 75) {
  tier = 'Hot';
  is_mql = true;
} else if (score >= 55) {
  tier = 'MQL';
  is_mql = true;
} else if (score >= 35) {
  tier = 'Nurture';
} else {
  tier = 'Low';
}

return {
  ...lead,
  lead_score: score,
  score_breakdown: scoreBreakdown,
  lead_tier: tier,
  is_mql: is_mql,
  scored_at: new Date().toISOString(),
  scoring_version: 'v2.0'
};

// Canonical Record Matching & Deduplication
// Ensures single record per unique person/company
const lead = $input.first().json;

// Normalize data for matching
const normalizedEmail = (lead.email || '').toLowerCase().trim();
const normalizedPhone = (lead.phone || '').replace(/\D/g, '');
const companyDomain = lead.company_domain || (lead.email ? lead.email.split('@')[1] : '');

// Matching rules (in priority order)
const matchingRules = {
  email: normalizedEmail,
  phone: normalizedPhone,
  company_domain: companyDomain,
  linkedin_url: lead.linkedin_url
};

// In production, this would query HubSpot + Google Sheets
// For now, simulate with flag
const isExisting = false; // Replace with actual lookup

return {
  ...lead,
  normalized_email: normalizedEmail,
  normalized_phone: normalizedPhone,
  company_domain: companyDomain,
  matching_rules: matchingRules,
  is_duplicate: isExisting,
  canonical_id: isExisting ? 'existing_id' : 'new_' + Date.now(),
  dedupe_timestamp: new Date().toISOString()
};

