  nurture_count: items.filter(i => !i.json.is_mql && i.json.lead_score >= 35).length,
  errors_count: 0, // Would be populated by error handler
  avg_lead_score: items.reduce((sum, i) => sum + (i.json.lead_score || 0), 0) / items.length
